<?php/** * Class action */class action {/** * CLASS PROPERTIES AND METHODS */    private function __construct() {    }/** * STATIC PROPERTIES AND METHODS */    public static function load_template($folder = "", $file = "", $arr = array()) {        $content = "";        $tfile   = APP . 'views' . DS . $folder . DS . $file . '.phtml';        if (file_exists($tfile)) {            ob_start();            foreach ($arr as $key => $value) {                $$key = $value;            }            require_once $tfile;            $content = ob_get_contents();            ob_clean();        }        echo $content;    }    public static function load_model($model) {        $efile = APP . "models" . DS . $model . '.php';        if (file_exists($efile)) {            require_once $efile;        }    }    public static function redirect($location) {        header(sprintf("Location: %s", url::site_url($location)));        exit;    }}